let b_lines =
  let r = Str.regexp_string "\n" in
  let ls = Str.split r banner_str in
  ls
;;

let banner () =
  let show_line l =
    Printf.printf "%s%!\n" l;
    ignore (Unix.select [] [] [] 0.01)
  in
  List.iter show_line b_lines;
  Trident
